{
	"model": "nfe",
	"rule": "rule0002",
	"description": "scapes e replaces para carta de correcao autorizada",
	"services": [{
			"service": "nfeRecepcaoEvento",
			"request": {
				"mode": "sync",
				"replaces": [
					{
						"tag": "idLote",
						"value": "[0-9]{1,15}",
						"type": "simple"
					},
					{
						"attribute": "Id",
						"value": "ID[0-9]{52}"
					},
				     {
						"tag": "chNFe",
						"value": "[0-9]{44}",
						"type": "simple"
					},
					{
						"tag": "dhEvento",
						"value": "([20])([0-9]{2,4})-[0-9]{2,2}-[0-9]{2,2}T[0-9]{2,2}:[0-9]{2,2}:[0-9]{2,2}-[0-9]{2,2}:[0-9]{2,2}",
						"type": "simple"
				     },
					{
						"tag": "nSeqEvento",
						"value": "[0-9]{1}",
						"type": "simple"
					},
					{
						"tag": "xCorrecao",
						"value": ".{15,}",
						"type": "simple"
					},
					{
						"tag": "xJust",
						"value": ".{15,}",
						"type": "simple"
					},
					{
						"tag": "nProt",
						"value": "[0-9]{15}",
						"type": "simple"
					},
					
					{
						"tag": "Signature",
						"value": ".*",
						"type": "complex"
					}
					
				]
			},
			"response": {
				"mode": "sync",
				"replaces": [
					{
						"tag": "idLote",
						"from": "getRandomNumber",
						"size": 15,
						"type": "simple"
					}, 
					{
						"tag": "chNFe",
						"from": "retRequest",
						"requestTag": "chNFe",
						"type": "simple"
					}, 
					{
						"tag": "nSeqEvento",
						"from": "retRequest",
						"requestTag": "nSeqEvento",
						"type": "simple"
					}, 
					{
						"tag": "dhRegEvento",
						"from": "getDateTime",
						"type": "simple"
					},
					{
						"tag": "nProt",
						"from": "getRandomNumber",
						"size": 15,
						"type": "simple"
					}
				]
			}
		}

	]

}
